import React, { useEffect, useState } from "react";
import Cookies from "./cookies.json";
import "./cookies.css";
import { Carousel } from 'antd';

interface Props {
  isMobile: boolean;
}

export default function OurCookies(props: Props) {
  const [modalOpen, setModalOpen] = useState(false);
  const [openPanel, setOpenPanel] = useState("");
  const [selectedCookie, setSelectedCookie] = useState("");

  var slideIndex = 0;
  var timer:any = null;

  if (document.getElementsByClassName("mySlides")) {
    showSlides();
  }

  useEffect(() => {
    console.log(timer);

    while (timer--) {
      console.log(`UseEffect clearing timer ${timer}`);
      window.clearTimeout(timer); // will do nothing if no timeout with id is present
  }
  }, [])

  function showSlides() {
    var i;
    var slides = Array.from(
      document.getElementsByClassName(
        "mySlides"
      ) as HTMLCollectionOf<HTMLElement>
    );

    console.log('showSlides timer');
    console.log(timer);

    while (timer--) {
      console.log(`showSlides clearing timer ${timer}`);
      window.clearTimeout(timer); // will do nothing if no timeout with id is present
  }

    if (slides.length > 0) {
      // var dots = document.getElementsByClassName("dot");
      for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
      }
      slideIndex++;
      if (slideIndex > slides.length) {
        slideIndex = 1;
      }
      // for (i = 0; i < dots.length; i++) {
      //   dots[i].className = dots[i].className.replace(" active", "");
      // }
      slides[slideIndex - 1].style.display = "block";
      /// dots[slideIndex - 1].className += " active";
       timer = setTimeout(showSlides, 6500); // Change image every 6.5 seconds
    }
  }

  function closeModal() {
    console.log('clearing timers');
    console.log(timer);

    while (timer--) {
      window.clearTimeout(timer); // will do nothing if no timeout with id is present
  }

    setModalOpen(false);
  }

  function onAccordionSelection(key: string) {
    if (key === openPanel) {
      setOpenPanel("");
      return;
    }

    setOpenPanel(key);
  }

  function ModalContent() {
    const contentStyle = {
      height: '160px',
      color: '#fff',
      lineHeight: '160px',
      textAlign: 'center',
      background: '#364d79',
    };

    return (
      <div className="modal-content">
        {/* <div className="slideshow-container">
          {Cookies
            .filter((cookie) => cookie.type === selectedCookie)
            .map((cookie, index) => {
              return (
                <div className="mySlides fade" key={index}>
                  <div className="numbertext">{index + 1}</div>
                  <img src={props.isMobile ? cookie.mobileUrl : cookie.url} style={{ width: "100%" }} />
                  <div className="text">{cookie.caption}</div>
                </div>
              );
            })}
        </div> */}
        <Carousel afterChange={() => console.log('carousel changing')}>
          <div>
            <h3 style={{height:'160px', color:'#fff', }}>1</h3>
          </div>
          <div>
            <h3 style={contentStyle}>2</h3>
          </div>
          <div>
            <h3 style={contentStyle}>3</h3>
          </div>
          <div>
            <h3 style={contentStyle}>4</h3>
          </div>
      </Carousel>
        <br />

        {/* <div style={{ textAlign: "center" }}>
          {cookiesToDisplay
            .filter((cookie) => cookie.type === selectedCookie)
            .map(() => {
              return <span className="dot"></span>;
            })}
        </div> */}
        <div>
          <button className="modal-btn" onClick={() => closeModal()}>
            Close
          </button>
          <a href="/order-inquiry">
            <button className="modal-btn" onClick={() => closeModal()}>
              Purchase
            </button>
          </a>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h1>Our Cookies</h1>
      <button
        className="accordion"
        onClick={() => {
          onAccordionSelection("christmas");
        }}
      >
        Christmas
      </button>
      <div
        className="panel"
        style={{ display: openPanel === "christmas" ? "block" : "none" }}
      >
        <div className="row">
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div
              className="card"
              key="santa"
              onClick={(e) => {
                setModalOpen(true);
                setSelectedCookie("santa");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Santa+Trio.JPG"}
                className={"card-img"}
              />
              <h3>{"Santa Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("tree");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Tree+Group.JPG"}
                className={"card-img"}
              />
              <h3>{"Christmas Tree Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("phrase");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Joy.JPG"}
                className={"card-img"}
              />
              <h3>{"Christmas Phrase Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("ornament");
              }}
            >
