import { Collapse } from "antd";
import React, { useState } from "react";
import "./cookies.css";

interface Props {
  isMobile: boolean;
}
const { Panel } = Collapse;

export default function OurCookies(props: Props) {
  const [modalOpen, setModalOpen] = useState(false);
  const [openPanel, setOpenPanel] = useState("");

  var slideIndex = 0;

  if (document.getElementsByClassName("mySlides")) {
    showSlides();
  }

  function showSlides() {
    var i;
    var slides = Array.from(
      document.getElementsByClassName(
        "mySlides"
      ) as HTMLCollectionOf<HTMLElement>
    );

    if (slides.length > 0) {
      var dots = document.getElementsByClassName("dot");
      for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
      }
      slideIndex++;
      if (slideIndex > slides.length) {
        slideIndex = 1;
      }
      for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
      }
      slides[slideIndex - 1].style.display = "block";
      dots[slideIndex - 1].className += " active";
      setTimeout(showSlides, 6500); // Change image every 2 seconds
    }
  }

  function onAccordionSelection(key: string) {
    if (key === openPanel) {
      setOpenPanel('');
      return;
    }

    setOpenPanel(key);
  }

  return (
    <div>
      <h1>Our Cookies</h1>
      <h2>Mmm Mmm Good... Say Less...</h2>
      <button
        className="accordion"
        onClick={() => {
          onAccordionSelection("christmas");
        }}
      >
        Christmas
      </button>
      <div
        className="panel"
        style={{ display: openPanel === "christmas" ? "block" : "none" }}
      >
        <div className="row">
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Santa+Trio.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Santa Cookies"}</h3>
              <p>{"For those you can't wait to see on Monday"}</p>
            </div>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Tree+Group.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Christmas Tree Cookies"}</h3>
              <p>{"Just need a glass of milk and a tray just for Santa"}</p>
            </div>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Joy.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Christmas Phrase Cookies"}</h3>
              <p>{"Just need a glass of milk and a tray just for Santa"}</p>
            </div>
          </div>
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Tech+Logo+No+Padding.png"
                }
                className={"card-img"}
              />
              <h3>{"Snowman Cookies"}</h3>
              <p>
                {
                  "Making life a little sweeter for that special someone in your life"
                }
              </p>
            </div>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Tech+Logo+No+Padding.png"
                }
                className={"card-img"}
              />
              <h3>{"Reindeer Cookies"}</h3>
              <p>
                {
                  "Making life a little sweeter for that special someone in your life"
                }
              </p>
            </div>
            <div className="card" onClick={() => setModalOpen(true)}>
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Holly+Berry1.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Holly Berry Cookies"}</h3>
              <p>
                {
