import React, { useEffect, useState } from "react";
import Cookies from "./cookies.json";
import "./cookies.css";
import { Carousel } from "antd";
import { clearTimeout } from "timers";

interface Props {
  isMobile: boolean;
}

export default function OurCookies(props: Props) {
  var slideIndex = 1;
  const [modalOpen, setModalOpen] = useState(false);
  const [openPanel, setOpenPanel] = useState("");
  const [selectedCookie, setSelectedCookie] = useState("");

  if (document.getElementsByClassName("mySlides")) {
    showSlides(1);
  }

  // Next/previous controls
  function plusSlides(n:number) {
    showSlides(slideIndex += n);
  }

  function showSlides(n:number) {
    var i;
    var slides = Array.from(
      document.getElementsByClassName(
        "mySlides"
      ) as HTMLCollectionOf<HTMLElement>
    );

    if (n) {
      if (n > slides.length) {slideIndex = 1} 
      if (n < 1) {slideIndex = slides.length}
    }
    console.log('slideIndex in showSlides')
    console.log(slideIndex)

    if (slides.length > 0) {
      console.log('inside if')
      // var dots = document.getElementsByClassName("dot");
      for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
      }

      slides[slideIndex - 1].style.display = "block";
      /// dots[slideIndex - 1].className += " active";
      // timer = setTimeout(showSlides, 2000); // Change image every 6.5 seconds
    }
  }

  function onAccordionSelection(key: string) {
    if (key === openPanel) {
      setOpenPanel("");
      return;
    }

    setOpenPanel(key);
  }

  function ModalContent() {
    let cookiesToShow = Cookies.filter((cookie) => cookie.type === selectedCookie);

    console.log(cookiesToShow);

    return (
      <div className="modal-content">
        <div className="slideshow-container">
          {cookiesToShow.map((cookie, index) => {
              return (
                <div className="mySlides fade" key={index}>
                  <div className="numbertext">{cookie.caption}</div>
              <img
                src={cookie.mobileUrl}
                style={{width:'100%'}}
              />
              <div className="text">{cookie.size}</div>
                </div>
              );
            })}
        </div>
        <br />
        <div>
          <button className="modal-btn" onClick={() => plusSlides(1)}>
            Next
          </button>
        </div>
        <div>
          <button className="modal-btn" onClick={() => {setModalOpen(false);}}>
            Close
          </button>
          <a href="/order-inquiry">
            <button className="modal-btn" onClick={() => setModalOpen(false)}>
              Purchase
            </button>
          </a>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h1>Our Cookies</h1>
      <button
        className="accordion"
        onClick={() => {
          onAccordionSelection("christmas");
        }}
      >
        Christmas
      </button>
      <div
        className="panel"
        style={{ display: openPanel === "christmas" ? "block" : "none" }}
      >
        <div className="row">
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div
              className="card"
              key="santa"
              onClick={(e) => {
                setModalOpen(true);
                setSelectedCookie("santa");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Santa+Trio.JPG"}
                className={"card-img"}
              />
              <h3>{"Santa Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("tree");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Tree+Group.JPG"}
                className={"card-img"}
              />
              <h3>{"Christmas Tree Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("phrase");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Joy.JPG"}
                className={"card-img"}
              />
              <h3>{"Christmas Phrase Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("ornament");
              }}
            >
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Stringed+Lights.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Christmas Ornament Cookies"}</h3>
            </div>
          </div>
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("snowman");
              }}
            >
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Mini+Frosty.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Snowman Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("stocking");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Stocking.JPG"}
                className={"card-img"}
              />
              <h3>{"Christmas Stocking Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("reindeer");
              }}
            >
              <img
                src={"https://wellcall-app-cdk.s3.amazonaws.com/Rudolph2.JPG"}
                className={"card-img"}
              />
              <h3>{"Reindeer Cookies"}</h3>
            </div>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("hollyberry");
              }}
            >
              <img
                src={
                  "https://wellcall-app-cdk.s3.amazonaws.com/Holly+Berry1.JPG"
                }
                className={"card-img"}
              />
              <h3>{"Holly Berry Cookies"}</h3>
            </div>
          </div>
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div
              className="card"
              onClick={() => {
                setModalOpen(true);
                setSelectedCookie("gingerbread");
              }}
            >
              <img
                src={
