import { Collapse } from "antd";
import React, { useEffect, useState } from "react";
import Cookies from "./cookies.json";
import CookiesMobile from "./cookies-mobile.json";
import "./cookies.css";

interface Props {
  isMobile: boolean;
}
const { Panel } = Collapse;

export default function OurCookies(props: Props) {
  const [modalOpen, setModalOpen] = useState(false);
  const [openPanel, setOpenPanel] = useState("");
  const [selectedCookie, setSelectedCookie] = useState("");
  const breakpoint = window.matchMedia("(max-width: 1279px)");
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    setIsMobile(breakpoint.matches);
  }, []);

  var slideIndex = 0;

  if (document.getElementsByClassName("mySlides")) {
    showSlides();
  }

  function showSlides() {
    var i;
    var slides = Array.from(
      document.getElementsByClassName(
        "mySlides"
      ) as HTMLCollectionOf<HTMLElement>
    );

    if (slides.length > 0) {
      var dots = document.getElementsByClassName("dot");
      for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
      }
      slideIndex++;
      if (slideIndex > slides.length) {
        slideIndex = 1;
      }
      for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
      }
      slides[slideIndex - 1].style.display = "block";
      dots[slideIndex - 1].className += " active";
      setTimeout(showSlides, 6500); // Change image every 2 seconds
    }
  }

  function onAccordionSelection(key: string) {
    if (key === openPanel) {
      setOpenPanel("");
      return;
    }

    setOpenPanel(key);
  }

  function ModalContent() {
    const cookiesToDisplay = isMobile ? CookiesMobile : Cookies;

    return (<div className="modal-content">
    <div className="slideshow-container">
      {cookiesToDisplay.filter((cookie) => cookie.type === selectedCookie).map(
        (cookie, index) => {
          return (
            <div className="mySlides fade" key={index}>
              <div className="numbertext">{index}</div>
              <img src={cookie.url} style={{ width: "100%" }} />
              <div className="text">Caption {index}</div>
            </div>
          );
        }
      )}
    </div>
    <br />

    <div style={{ textAlign: "center" }}>
      {cookiesToDisplay.filter((cookie) => cookie.type === selectedCookie).map(
        () => {
          return <span className="dot"></span>;
        }
      )}
    </div>
    <div>
      <button className="modal-btn" onClick={() => setModalOpen(false)}>
        Close
      </button>
      <a href="/order-inquiry">
        <button className="modal-btn" onClick={() => setModalOpen(false)}>
          Purchase
        </button>
      </a>
    </div>
  </div>

    )
  }

  return (
    <div>
      <h1>Our Cookies</h1>
      <h2>Mmm Mmm Good... Say Less...</h2>
      <button
        className="accordion"
        onClick={() => {
          onAccordionSelection("christmas");
        }}
      >
        Christmas
      </button>
      <div
        className="panel"
        style={{ display: openPanel === "christmas" ? "block" : "none" }}
      >
        <div className="row">
          <div className={props.isMobile ? "col-12" : "col-4"}>
            <div
              className="card"
              key="santa"
              onClick={(e) => {
                setModalOpen(true);
                setSelectedCookie("santa");
              }}
            >
